// Find agent - usando regex para pegar linha correta
let targetLine = null;
for (let i = 1; i < lines.length; i++) {
  const line = lines[i];
  if (line.includes(workflowId) && line.includes(projectId) && line.includes(agentId)) {
    targetLine = line;
    console.log('âœ… Target line found:', line.substring(0, 150) + '...');
    break;
  }
}

if (!targetLine) {
  return [{
    json: {
      success: false,
      error: 'Agent nÃ£o encontrado',
      search: { workflowId, projectId, agentId },
      available_lines: lines.map((line, i) => `${i}: ${line.substring(0, 100)}`)
    }
  }];
}

// Parse apenas os campos que conseguimos extrair com seguranÃ§a
// Vai buscar os primeiros campos fixos antes do JSON complexo
