// Graph Processor - TESTE BÃSICO
const inputData = $('Webhook Enhanced').item.json.body;
const csvData = $('Load Graph CSV').item.json.data;

console.log('ðŸš€ TESTE BÃSICO - Graph Processor');
console.log('ðŸ“¥ Input received:', inputData);
console.log('ðŸ“„ CSV loaded:', csvData ? 'YES' : 'NO', csvData ? csvData.length : 0, 'chars');

if (!csvData) {
  return [{
    json: {
      success: false,
      error: 'CSV not loaded',
      test_step: 'csv_loading',
      timestamp: new Date().toISOString()
    }
  }];
}

const projectId = inputData.project_id;
const agentId = inputData.agent_id;
const query = inputData.query || 'Default query';
const workflowId = 'uptax-proc-1001-dynamic';

if (!projectId || !agentId) {
  return [{
    json: {
      success: false,
      error: 'Missing project_id or agent_id',
      received: inputData,
      test_step: 'input_validation',
      timestamp: new Date().toISOString()
    }
  }];
}

// Parse CSV - FIXED VERSION for multi-MCP
console.log('ðŸ“Š Parsing CSV data...');
const lines = csvData.split('\n').filter(line => line.trim());
console.log('ðŸ“‹ CSV lines found:', lines.length);

let agentConfig = null;

for (let i = 1; i < lines.length; i++) {
  const line = lines[i];
  console.log(`ðŸ” Processing line ${i}:`, line.substring(0, 100) + '...');

  // Split preserving JSON content - find positions of key commas
  const parts = [];
  let current = '';
  let inQuotes = false;
  let inBrackets = 0;

  for (let j = 0; j < line.length; j++) {
    const char = line[j];
    if (char === '"') inQuotes = !inQuotes;
    if (!inQuotes && char === '[') inBrackets++;
    if (!inQuotes && char === ']') inBrackets--;

    if (!inQuotes && inBrackets === 0 && char === ',') {
      parts.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }
  if (current) parts.push(current.trim());

  console.log(`ðŸ” Checking parsed:`, parts[0], parts[1], parts[2]);

  if (parts[0] === workflowId && parts[1] === projectId && parts[2] === agentId) {
    console.log('âœ… Match found!');
    agentConfig = {
      workflow_id: parts[0],
      project_id: parts[1],
      agent_id: parts[2],
      agent_type: parts[3],
      description: parts[4],
      mcp_endpoints_raw: parts[5], // Keep raw for debugging
      prompt_url: parts[6],
      processor_url: parts[7],
      tools_config_url: parts[8]
    };
    break;
  }
}

if (!agentConfig) {
  return [{
    json: {
      success: false,
      error: `No config found for ${workflowId}/${projectId}/${agentId}`,
      csv_rows: lines.length - 1,
      test_step: 'agent_lookup',
      search_criteria: { workflowId, projectId, agentId },
      timestamp: new Date().toISOString()
    }
  }];
}

console.log('âœ… Agent config found:', agentConfig.agent_type);

// TESTE: Tentar carregar prompt do GitHub usando N8N $http
let promptTest = {
  url: agentConfig.prompt_url,
  loaded: false,
  length: 0,
  error: null
};

try {
  console.log('ðŸŒ Testing prompt loading from:', agentConfig.prompt_url);

  // Use N8N's built-in $http instead of fetch
  const response = await $http.get(agentConfig.prompt_url, {
    headers: {
      'User-Agent': 'N8N-Graph-Processor/1.0'
    },
    timeout: 10000
  });

  if (response && response.data) {
    const promptContent = typeof response.data === 'string' ? response.data : JSON.stringify(response.data);
    promptTest.loaded = true;
    promptTest.length = promptContent.length;
    console.log('âœ… Prompt loaded successfully with $http:', promptTest.length, 'chars');
  } else {
    promptTest.error = 'No data in response';
    console.log('âŒ No data in response');
  }
} catch (error) {
  promptTest.error = error.message || 'Unknown error';
  console.log('âŒ Prompt load error:', promptTest.error);

  // Fallback: skip prompt loading for now
  promptTest.url = agentConfig.prompt_url;
  promptTest.loaded = false;
  promptTest.error = 'Will be loaded in AI Agent step';
}

const sessionId = `${projectId}_${agentId}_${Date.now()}`;

console.log('ðŸŽ¯ TESTE BÃSICO CONCLUÃDO');

return [{
  json: {
    success: true,
    test_step: 'complete',
    input: {
      project_id: projectId,
      agent_id: agentId,
      query: query
    },
    csv_test: {
      loaded: true,
      lines: lines.length - 1,
      agent_found: true
    },
    agent_config: agentConfig,
    prompt_test: promptTest,
    session_id: sessionId,
    timestamp: new Date().toISOString(),
    version: 'test-basic-1.0'
  }
}];
