// Graph CSV Parser - Find project_id + agent_id combination
const projectId = $input.all()[0].json.project_id;
const agentId = $input.all()[0].json.agent_id;
const query = $input.all()[0].json.query;

if (!projectId || !agentId) {
  return [{
    json: {
      error_occurred: true,
      message: "Missing required parameters: project_id and agent_id",
      graph_key: "invalid"
    }
  }];
}

// Generate graph key
const graphKey = `${projectId}_${agentId}_${Date.now()}`;

return [{
  json: {
    project_id: projectId,
    agent_id: agentId,
    query: query,
    graph_key: graphKey,
    csv_url: "https://raw.githubusercontent.com/Uptax-creator/N8N-Research-Agents/main/assembly-logic/agents-registry-graph.csv"
  }
}];

// Parse CSV and find matching workflow_id + project_id + agent_id
const input = $input.all()[0].json;
const csvData = input.data;
const projectId = input.project_id;
const agentId = input.agent_id;
const workflowId = 'uptax-proc-1001-dynamic'; // Current workflow

// Parse CSV
const lines = csvData.split('\n').filter(line => line.trim());
let agentConfig = null;

// Find matching row (skip header)
for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(',').map(v => v.trim());
  if (values[0] === workflowId && values[1] === projectId && values[2] === agentId) {
    agentConfig = {
      workflow_id: values[0],
      project_id: values[1],
      agent_id: values[2],
      agent_type: values[3],
      description: values[4],
      prompt_url: values[5],
      processor_url: values[6],
      mcp_endpoint: values[7],
      tools_config_url: values[8]
    };
    break;
  }
}

if (!agentConfig) {
  return [{
    json: {
      error_occurred: true,
      message: `No configuration found for workflow: ${workflowId}, project_id: ${projectId}, agent_id: ${agentId}`,
      graph_key: input.graph_key
    }
  }];
}

return [{
  json: {
    ...input,
    agent_config: agentConfig,
    found_match: true
  }
}];

// Prepare final agent configuration
const input = $input.all()[0].json;
const toolsConfig = input.data;

let parsedToolsConfig;
try {
  parsedToolsConfig = JSON.parse(toolsConfig);
} catch (error) {
  return [{
    json: {
      error_occurred: true,
      message: `Failed to parse tools configuration: ${error.message}`,
      graph_key: input.graph_key
    }
  }];
}

return [{
  json: {
    project_id: input.project_id,
    agent_id: input.agent_id,
    query: input.query,
    graph_key: input.graph_key,
    agent_config: input.agent_config,
    tools_config: parsedToolsConfig,
    mcp_endpoint: input.agent_config.mcp_endpoint,
    agent_type: input.agent_config.agent_type,
    ready_for_execution: true
  }
}];

// ✅ FIXED VERSION - Response Formatter Node
// Correção para evitar chave reservada 'error' no output

// Dynamic Response Formatter - Loaded from GitHub
try {
  const formatterCode = $('load-response-formatter').item.json.data;
  const formatter = eval(formatterCode);
  const aiResponse = $('ai-agent').item.json;
  const result = formatter.formatResponse(aiResponse, $input.item.json);

  // ✅ Retorna resultado encapsulado corretamente
  return [{
    json: {
      ...result,
      formatted_at: new Date().toISOString(),
      status: "success"
    }
  }];

} catch (error) {
  console.error('Response formatter error:', error);

  // ✅ CORREÇÃO: Evitar chaves reservadas + estrutura correta
  return [{
    json: {
      success: false,
      agent: 'enhanced_research',
      error_occurred: true,           // ❌ era: error: error.message
      error_message: error.message,   // Renomeado de 'error'
      error_details: error.stack || error.toString(),
      timestamp: new Date().toISOString(),
      status: "formatter_error",
      fallback_data: {
        ai_response: $('ai-agent').item.json || null,
        input_data: $input.item.json || null
      }
    }
  }];
}
